import { useState, useEffect } from "react";

import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";
import useHttp from "../../hooks/use-http";

const DUMMY_MEALS = [
  // {
  //   id: "m1",
  //   name: "Sushi",
  //   description: "Finest fish and veggies",
  //   price: 22.99,
  // },
  // {
  //   id: "m2",
  //   name: "Schnitzel",
  //   description: "A german specialty!",
  //   price: 16.5,
  // },
  // {
  //   id: "m3",
  //   name: "Barbecue Burger",
  //   description: "American, raw, meaty",
  //   price: 12.99,
  // },
  // {
  //   id: "m4",
  //   name: "Green Bowl",
  //   description: "Healthy...and green...",
  //   price: 18.99,
  // },
];

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);

  const { isLoading, error, sendRequest: fetchMeals } = useHttp();

  useEffect(() => {
    const transformMeals = (mealsObj) => {
      const loadedMeals = [];

      for (const mealKey in mealsObj) {
        loadedMeals.push({
          id: mealKey,
          name: mealsObj[mealKey].name,
          description: mealsObj[mealKey].description,
          price: mealsObj[mealKey].price,
        });
      }

      setMeals(loadedMeals);
    };

    fetchMeals(
      {
        url: "https://react-http-499de-default-rtdb.europe-west1.firebasedatabase.app/meals.json",
      },
      transformMeals
    );
  }, [fetchMeals]);

  const mealsList = meals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  let context = <p>Found no meals.</p>;

  if (isLoading) {
    context = <p>Loading...</p>;
  }

  if (error) {
    context = <p>{error}</p>;
  }

  if (meals.length > 0) {
    context = (
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    );
  }

  return <section className={classes.meals}>{context}</section>;
};

export default AvailableMeals;
